CREATE TABLE IF NOT EXISTS `cscart_brand_ignore_list` (
    `brand_id` mediumint(8) unsigned NOT NULL,
    `ignore_list` text NOT NULL,
    PRIMARY KEY (`brand_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `cscart_technologies` (
    `technology_id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
    `name` varchar(255) NOT NULL,
    `description` mediumtext NOT NULL,
    PRIMARY KEY (`technology_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE IF NOT EXISTS `cscart_warehouses` (
    `warehouse_id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
    `name` varchar(255) NOT NULL,
    `priority` smallint(5) unsigned NOT NULL,
    PRIMARY KEY (`warehouse_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE `cscart_warehouse_brands` (
    `pair_id` mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT,
    `warehouse_id` mediumint(8) UNSIGNED NOT NULL,
    `brand_id` mediumint(8) UNSIGNED NOT NULL,
    PRIMARY KEY (`pair_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
